  display: flex;
  width: 100%;
  height: 100vh;
  justify-content: center;
  align-items: center;
  background-color: ${(props) => props.theme.backgroundColor};
`;

const rotateAnimation = keyframes`
  0% {
    transform: rotate(0deg);
    border-radius: 0;
  }
  50% {
    border-radius: 100px;
    }
    100% {
      transform: rotate(360deg);
    border-radius: 0;
    }
`;

const Emoji = styled.span`
  font-size: 36px;

  &:active {
    opacity: 0;
  }
`;

