  display: flex;
  justify-content: center;
  align-items: center;
  width: 200px;
  height: 200px;
  background-color: paleturquoise;
  animation: ${rotateAnimation} 1s linear infinite;

  ${Emoji} {
    &:hover{
      font-size: 60px;
    }
  }
`;

